 import { Provider, IAgentRuntime, Memory, State } from &quot;@ai16z/eliza&quot;; const comprehensiveProvider: Provider = { get: async (runtime: IAgentRuntime, message: Memory, state?: State) =&gt; { try { // Get recent messages const messages = await runtime.messageManager.getMemories({ roomId: message.roomId, count: 5, }); // Get user context const userContext = await runtime.descriptionManager.getMemories({ roomId: message.roomId, userId: message.userId, }); // Get relevant facts const facts = await runtime.messageManager.getMemories({ roomId: message.roomId, tableName: &quot;facts&quot;, count: 3, }); // Format comprehensive context return ` # Conversation Context ${messages.map((m) =&gt; `- ${m.content.text}`).join(&quot;
&quot;)} # User Information ${userContext.map((c) =&gt; c.content.text).join(&quot;
&quot;)} # Related Facts ${facts.map((f) =&gt; `- ${f.content.text}`).join(&quot;
&quot;)} `.trim(); } catch (error) { console.error(&quot;Provider error:&quot;, error); return &quot;Context temporarily unavailable&quot;; } }, };
